void Sound_Init(uint32_t period, uint32_t priority) {
  // write this
  DAC5_Init();
  SysTick->CTRL = 0x00; // disable SysTick during setup

  SysTick->LOAD = period - 1; // reload value
  SCB->SHP[1] = (SCB->SHP[1] & (~0xC0000000)) | (priority << 30); // priority 2
  SysTick->VAL = 0; // any write to VAL clears COUNT and sets VAL equal to LOAD
  SysTick->CTRL = 0x07; // enable SysTick with 80 MHz bus clock and interrupts
  __enable_irq();
  globalIndex = 0;
}
void Sound_Stop(void) {
  // either set LOAD to 0 or clear bit 1 in CTRL
  SysTick->LOAD = 0;
}

void Sound_Start(uint32_t period) {
  SysTick->LOAD = period - 1;
  SysTick->VAL = 0;
}

void Sound_lose(void) {
  globalIndex = 0;  // Reset the index to start from beginning
  Sound_Start(12259);  // Use the correct period for the length of lose array
}

// Interrupt service routine
// Executed every 12.5ns*(period)
void SysTick_Handler(void) {
  uint8_t data;
  uint32_t count;
  
  if(SoundFlag == 1){
    data = lose[globalIndex];
    count = 12259;
  }else{
    data = coin[globalIndex];
    count = 6802;
  }

  DAC5_Out(data);
  globalIndex++;

  if (globalIndex >= count) {
    globalIndex = 0;
    Sound_Stop();  // Stop sound when finished playing
  }
}
 void pleaseWork(void){
    __disable_irq();
    Sound_Init(1,0);  // initialize sound
    __enable_irq();
    if(SoundFlag == 1){
      Sound_Start(12259);
    }else{
      Sound_Start(6802);
    }
    while(1){
      break;
    }
 }